#rtxxkagwac .gt_from_md &gt; :first-child { margin-top: 0; }
#rtxxkagwac .gt_from_md &gt; :last-child { margin-bottom: 0; }
#rtxxkagwac .gt_row { padding-top: 8px; padding-bottom: 8px; padding-left: 5px; padding-right: 5px; margin: 10px; border-top-style: solid; border-top-width: 1px; border-top-color: #D3D3D3; border-left-style: none; border-left-width: 1px; border-left-color: #D3D3D3; border-right-style: none; border-right-width: 1px; border-right-color: #D3D3D3; vertical-align: middle; overflow-x: hidden; }
